func (c *current) onBRollList1(first, rest interface{}) (interface{}, error) {
	bRollList := ast.NewBRollList2(first.(*ast.BRoll))

	for _, r := range rest.([]interface{}) {
		rs := r.([]interface{})
		b := rs[1].(*ast.BRoll)
		bRollList.Append(b)
	}

	return bRollList, nil
}

